remarks.code:
void MakeBox (Scene scene)
{
    var node = scene.CreateChild ();
    node.Position = new Vector3 (0,0,0);
    node.SetScale(1);
    node.Rotation = new Quaternion(60, 0, 30);

    // Create a Box Shape component:
    var box = node.CreateComponent<Box> ();
    box.Color = Color.Blue;
}
